import React, { Component } from 'react';
import './style.css';

import Btn from './button';
import Inp from './input';
import GetData from './getdata';


class App extends Component {
	constructor() {
		super();
		this._value = ''
		this._downlaod = this._downlaod.bind(this);
	}

	_changed = (state) => {
		console.log(this._value)
		this._value = (state.target.value);
		console.log(this._value)
	}

	_downlaod() {
		console.log('Dow', this._value)
		var val = this._value
		var data = {
			fname: this._value
		}

		serialize = function(obj) {
			var str = [];
			for (var p in obj)
			  if (obj.hasOwnProperty(p)) {
				str.push(encodeURIComponent(p) + "=" + encodeURIComponent(obj[p]));
			  }
			return str.join("&");
		  }

		console.log(data)

		fetch('http://creepyfuck.tech/downjson' + serialize(data), {
			method: 'POST',
		})
			.then(response => response.json())
			.then(data => {
				console.log('Success:', data);
			})
			.catch((error) => {
				console.error('Error:', error);
			});
	}

	render() {
		return (
			<div>
				<div className="side-padding-20">
					<Inp
						_onChange={this._changed}
					/>
					<br />
					<br />
					<Btn 
						_onClick={this._downlaod}
						_text={'Download'}
					/>
				</div>

				<br />
				<div>

				</div>
			</div>
		);
	}
}

export default App
