					.then(data => {
						console.log('Success:', data);
						setList(data)
					})
					.catch((error) => {
						console.error('Error:', error);
					});

				//setList(values)

				console.log(list_values)
			});
		}
	}

	promiseOptions = inputValue =>
		new Promise(resolve => {
			setTimeout(() => {
				resolve([
					{ label: "Hi", value: 1 },
					{ label: "Hello", value: 1 }
				]);
			}, 1000);
		});

	render() {
		return (
			<AsyncSelect cacheOptions defaultOptions loadOptions={this.promiseOptions} />
			// <Combo />
		);
	}
}

export default App
