import '@progress/kendo-theme-default/dist/all.css';
import React, { Component } from 'react';
import './style.css';
import Combo from './search'
import Btn from './mui_button';
import Inp from './mui_input';
import Select from 'react-select';
import AsyncSelect from 'react-select/async';

class App extends Component {
	constructor() {
		super();
		this._value = ''
		this.promiseOptions = this._enterd_data.bind(this);
	}

	_enterd_data(e) {
		var data = e

		if (data.length > 3) {
			console.log(data)

			var link = 'https://mangaowl.net/live_search/' + encodeURIComponent(data)
			console.log('LINK', link)

			return new Promise(resolve => {

				fetch(link, {
					method: 'GET',
					//mode: 'no-cors',
				})
					.then(response => response.json())
					.then(data => {
						console.log('Success:', data);
						var list = []
						data.map((el) => {
							list.push({
								label:el.name,
								value:el.url
							})
						})

						resolve(list)
						//setList(data)
					})
					.catch((error) => {
						console.error('Error:', error);
					});

				//setList(values)

				//console.log(list_values)
			});
		} else {
			return new Promise(resolve => {
				resolve([
					//{ label: "Hi", value: 1 },
					//{ label: "Hello", value: 1 }
				])
			});
		}
	}

	promiseOptions = inputValue =>
		new Promise(resolve => {
			setTimeout(() => {
				resolve([
					{ label: "Hi", value: 1 },
					{ label: "Hello", value: 1 }
				]);
			}, 1000);
		});

	render() {
		return (
			<div style={{width:"70%", textAlign:'center', marginRight:'15%', marginLeft:"15%"}}>
			<AsyncSelect cacheOptions defaultOptions loadOptions={this.promiseOptions} />
			</div>
			// <Combo />
		);
	}
}

export default App
