import React, { Component } from 'react';
import { render } from 'react-dom';
import Hello from './Hello';
import './style.css';

import RichEditorExample from './draft';
import GetData from './getdata';


class App extends Component {
	constructor() {
		super();
		this.state = {
			name: 'React'
		};
	}

	saving_function(raw_state) {
		const requestOptions = {
			method: 'POST',
			headers: { 'Content-Type': 'application/json' },
			body: JSON.stringify({ title: 'ashahj' })
		};

		fetch('http://localhost:4545/add', requestOptions)
			.then(response => response.json())
			.then(data => this.setState({ postId: data.id }));

	}
	//<Hello name={this.state.name} />
	//
	render() {
		return (
			<div>

				<div className="side-padding-20">
					<RichEditorExample
						my_func={this.saving_function}
						editorState={this.state.editorState}
						raw_state={this.raw_state}
						onChange={this.onChange}
						handleBeforeInput={this._handleBeforeInput}
						handlePastedText={this._handlePastedText}
					/>
				</div>

				<br />
				<div>

				</div>
			</div>
		);
	}
}

render(<App />, document.getElementById('root'));
