import '@progress/kendo-theme-default/dist/all.css';
import React, { Component } from 'react';
import './style.css';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import ListItemAvatar from '@material-ui/core/ListItemAvatar';
import ListItemText from '@material-ui/core/ListItemText';
import Btn from './mui_button';
import Inp from './mui_input';
import GetData from './getdata';
import grey from '@material-ui/core/colors/grey'
import { makeStyles, ThemeProvider } from '@material-ui/core/styles';
import {createMuiTheme} from '@material-ui/core';

var grey400 = grey
grey400.main = grey400[500]

const theme = createMuiTheme({
    palette: {
    //  primary: 'white',
      secondary: grey400,
    },
    status: {
      danger: 'orange',
    },
  });

var bookmark_test = [
    {
        name: 'Hello',
        link: 'http://creepyfuck.tech/react'
    },
    {
        name: 'Hello _2',
        link: 'http://creepyfuck.tech/react'
    },
]

class App extends Component {
    constructor(props) {
        super(props);
        this._bm_name = ''
        this._bm_val = ''

        this.state = {list: bookmark_test}

        this._get_list()
    }

    _get_list() {
        console.log('timeout')
        setTimeout(() => {
            console.log('timeouted')

            this.state.list.push({
                name: 'Dude',
                link:'sweet'
            })

            this.setState({
                list: this.state.list
            })

        }, 5000);
    }

    handleListItemClick = (item) => {
        var win = window.open(item.link, '_blank');
        win.focus();
    }

    _changed_name = (state) => {
        this._bm_name = (state.target.value);
    }

    _changed_val = (state) => {
        this._bm_val = (state.target.value);
    }

    _add() {
        var data = {
            name: this._bm_name,
            link: this._bm_val,
        }

        fetch('http://localhost:4545/react/addbookmark', {
            method: 'POST',
            body: data
        })
            .then(response => response.json())
            .then(data => {
                console.log('Success:', data);
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    render() {

        console.log(this.state.list)

        return (
            <ThemeProvider theme={theme}>
            <div>
                {/* <img src='/comic.png' height='100px' /> */}
                <div className="side-padding-20">
                    <Inp
                        _onChange={this._changed_name}
                        _label={'Name'}
                    />
                    <Inp
                        _onChange={this._changed_val}
                        _label={'Link'}
                    />
                    <Btn
                        _onClick={this._add}
                        _text={'Add'}
                    />
                    <List>
                        {this.state.list.map((bookmark) => (
                            <ListItem button style={{color:'white', textSecondary:'red'}} onClick={() => this.handleListItemClick(bookmark)} key={bookmark}>
                                <ListItemText secondary={bookmark.link} primary={bookmark.name} secondaryTypographyProps={{color:'secondary'}}/>
                            </ListItem>
                        ))}
                    </List>
                </div>

                <br />
            </div>
            </ThemeProvider>
        );
    }
}

export default App
