import React, { Component } from 'react';
import { Input, ButtonGroup, SplitButton, SplitButtonItem, ToolbarSpacer, DropDownButton, DropDownButtonItem, Toolbar, ToolbarItem, ToolbarSeparator } from '@progress/kendo-react-buttons'

class ButtonContainer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            logs: []
        };
    }

    handleDomEvent = (event) => {
        let logs = this.state.logs.slice();

        logs.unshift(event.type)

        this.setState({logs: logs})
    }
    render() {
        return ([
            <Input />,

        ])
    }
}

export default ButtonContainer
